export const fetchDataAction = ({ url }) => {
  return (dispatch) => {
    fetch(url)
      .then((payload) => {
        dispatch({
          type: "SET_SUCCESS_RESPONSE",
          payload,
          id: url,
        });
      })
      .catch((err) => {
        dispatch({
          type: "SET_FAILURE_RESPONSE",
          payload: "Fetch failed",
          id: url,
        });
      });
  };
};
