import React, { useState } from "react";
import PropTypes from "prop-types";
import { useDispatch, useSelector } from "react-redux";
import { setFavMatch, deleteFavMatch } from "../../Redux/Actions";
import styled from "styled-components";
import { device } from "../../objects/devices";
import MdStarOutline from "react-ionicons/lib/MdStarOutline";
import MdStar from "react-ionicons/lib/MdStar";
import { CSSTransition } from "react-transition-group";

const EachLeagueStyle = styled.div`
  display: grid;
  grid-template-columns: 60px 1fr 60px 1fr 60px;
  margin: 2px 0px;
  width: 100%;
  border-radius: 4px;
  @media ${device.tablet} {
    grid-template-columns: 55px auto 60px auto 20px;
  }
`;

const TimerStyle = styled.span`
  justify-self: start;
  align-self: center;
  color: rgba(255, 255, 255, 0.6);
  font-size: 14px;
`;

const ResultStyle = styled.span`
  display: flex;
  justify-content: center;
  align-items: center;
  justify-self: center;
  align-self: center;
  text-align: center;
  padding: 4px 6px;
  border-radius: 3px;
  color: #fff;
  background-color: #192c38;
  font-size: 13px;
`;

const TeamOne = styled.span`
  color: rgba(255, 255, 255, 0.85);
  font-size: 14px;
  justify-self: end;
  align-self: center;
`;

const TeamTwo = styled.span`
  color: rgba(255, 255, 255, 0.85);
  font-size: 14px;
  justify-self: start;
  align-self: center;
`;

const MatchResult = ({
  time,
  id,
  hometeam,
  awayteam,
  winner,
  homescore,
  awayscore,
}) => {
  const dispatch = useDispatch();
  const status = useSelector((state) => state.favMatchReducer);
  const [fade, setfade] = useState(false);

  return (
    <CSSTransition>
      <EachLeagueStyle>
        <TimerStyle>{time.slice(11, -4)}</TimerStyle>
        <TeamOne style={{ fontWeight: winner === "HOME_TEAM" ? "500" : "" }}>
          {hometeam}
        </TeamOne>
        <ResultStyle>
          {homescore} - {awayscore}
        </ResultStyle>
        <TeamTwo style={{ fontWeight: winner === "AWAY_TEAM" ? "500" : "" }}>
          {awayteam}
        </TeamTwo>
        {status.some((each) => each.id === id) ? (
          <MdStar
            style={{
              cursor: "pointer",
              justifySelf: "end",
              alignSelf: "center",
            }}
            color="#F4E122"
            fontSize="18px"
            onClick={() => {
              dispatch(
                deleteFavMatch({
                  time,
                  id,
                  hometeam,
                  awayteam,
                  winner,
                  homescore,
                  awayscore,
                })
              );
            }}
          />
        ) : (
          <MdStarOutline
            style={{
              cursor: "pointer",
              justifySelf: "end",
              alignSelf: "center",
            }}
            color="#F4E122"
            fontSize="18px"
            onClick={() => {
              dispatch(
                setFavMatch({
                  id,
                  time,
                  hometeam,
                  awayteam,
                  homescore,
                  awayscore,
                })
              );
            }}
          />
        )}
      </EachLeagueStyle>
    </CSSTransition>
  );
};

MatchResult.propTypes = {
  id: PropTypes.number,
  time: PropTypes.string,
  hometeam: PropTypes.string,
  awayteam: PropTypes.string,
  winner: PropTypes.string,
  homescore: PropTypes.number,
  awayscore: PropTypes.number,
};

export default MatchResult;
