export const favMatchReducer = (state = [], action) => {
  switch (action.type) {
    case "SET_FAV_MATCH":
      return state.some(
        ({ id, active }) =>
          id === action.payload.id && active === action.payload.active
      )
        ? state
        : [...state, { [action.id]: action.payload }];
    default:
      return state;
  }
};
