const initialState = [];

export const favMatchReducer = (state = initialState, action) => {
  switch (action.type) {
    case "SET_FAV_MATCH":
      return state.some(
        ({ id, active }) =>
          id === action.payload.id && active === action.payload.active
      )
        ? state
        : [...state, action.payload];
    // case "SET_FAV_MATCH":
    //   return state.some(
    //     ({ id, active }) =>
    //       id === action.payload.id && active === action.payload.active
    //   )
    //     ? state
    //     : [...state, action.payload];
    default:
      return state;
  }
};
