export const showReducer = (state = { showReducer: true }, action) => {
  switch (action.type) {
    case "SET_SHOW_MORE":
      return {
        showReducer: !state.showReducer,
      };
    default:
      return state;
  }
};

export const showFavReducer = (state = { showFavReducer: true }, action) => {
  switch (action.type) {
    case "SET_SHOW_FAV_MORE":
      return {
        showFavReducer: !state.showFavReducer,
      };
    default:
      return state;
  }
};
