export const fetchDataAction = ({ url }) => {
  return (dispatch) => {
    fetch(url, {
      method: "GET",
      headers: {
        "x-rapidapi-host": "api-football-v1.p.rapidapi.com",
        "x-rapidapi-key": "68eb2fd38fmsh44a836a4cc5e6f6p143577jsn22aa790dbab0",
      },
    })
      .then((res) => res.json())
      .then((payload) => {
        try {
          dispatch({
            type: "SET_SUCCESS_RESPONSE",
            payload,
            id: url,
          });
        } catch (err) {
          dispatch({
            type: "SET_SUCCESS_RESPONSE",
            payload: [], // dummy need to be proper eror response
            id: url,
          });
        }
      });
  };
};

