import React, { useState } from "react";
import styled from "styled-components";
import { testLib } from "../../objects/testLib";

const ContainerDivStyle = styled.div`
  display: flex;
  align-items: center;
  position: relative;
  top: 0;
  background-color: #192c38;
  border-radius: 4px;
  padding: 5px;
  justify-content: center;
  & input {
    color: rgba(255, 255, 255, 0.7);
    padding: 5px 5px;
    justify-content: flex-end;
    background-color: #213743;
    border-radius: 3px;
    width: 100%;
    min-width: 170px;
    border-color: transparent;
    outline: none;
  }
`;

const SearchDivStyle = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  position: absolute;
  top: 0;
  background-color: #192c38;
  border-radius: 4px;
  padding: 5px;
  justify-content: center;
  &:hover {
    box-shadow: 0px 0px 3px 0px rgba(0, 0, 0, 1);
  }
  & input {
    color: rgba(255, 255, 255, 0.7);
    padding: 5px 5px;
    justify-content: flex-end;
    background-color: #213743;
    border-radius: 3px;
    width: 100%;
    min-width: 170px;
    border-color: transparent;
    outline: none;
  }
`;

const ListDivStyle = styled.div`
  display: flex;
  flex-direction: column;
  padding: 10px;
  width: 100%;
`;

const SearchBar = () => {
  const [keyword, setkeyword] = useState("");

  const keywordChange = (e) => {
    setkeyword(e.target.value);
  };

  const ref = React.useRef();

  return (
    <ContainerDivStyle>
      <input
        type="text"
        onChange={keywordChange}
        value={keyword}
        placeholder="Search..."
        ref={ref}
      />
      <SearchDivStyle>
        <input
          type="text"
          onChange={keywordChange}
          value={keyword}
          placeholder="Search..."
          ref={ref}
        />
        <ListDivStyle>
          {testLib.map((each, i) => (
            <li key={i}>each</li>
          ))}
        </ListDivStyle>
      </SearchDivStyle>
    </ContainerDivStyle>
  );
};

export default SearchBar;
