export const favMatchReducer = (state = [], action) => {
  switch (action.type) {
    case "SET_FAV_MATCH":
      let index = state.findIndex((el) => el.event_id == action.event.event_id);

      if (index == -1) return [...state, action.event];
      return state;
    default:
      return state;
  }
};
