import React, { useState } from "react";
import MdClose from "react-ionicons/lib/MdClose";
import styled from "styled-components";
import Modal from "react-modal";

const CloseDivStyle = styled.div`
  display: flex;
  justify-content: flex-end;
  align-items: center;
`;

const LoginDivStyle = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 20px;
  & form {
    font-size: 14px;
    display: flex;
    flex-direction: column;
    & span {
      margin: 5px 0px;
    }
    & input {
      color: #fff;
      font-size: 14px;
      background: #0f212e;
      box-shadow: none;
      outline-style: none;
      border-color: transparent;
      border-radius: 3px;
      padding: 3px;
      margin: 6px 0px;
    }
  }
`;

const Sign = () => {
  Modal.setAppElement("#webapp");

  const closeModal = () => {
    return isOpen === false;
  };

  return (
    <div>
      <Modal
        isOpen={isOpen === true}
        onRequestClose={closeModal}
        style={customStyles}
      >
        <CloseDivStyle>
          <MdClose
            style={{ cursor: "pointer" }}
            color="rgba(255, 255, 255, 0.6)"
            fontSize="18px"
            onClick={closeModal}
          />
        </CloseDivStyle>
        <LoginDivStyle>
          <form>
            <span>Email</span>
            <input type="text" />
            <span>Password</span>
            <input type="password" />
          </form>
        </LoginDivStyle>
      </Modal>
    </div>
  );
};

export default Sign;
