import React from "react";
import { Switch, Route } from "react-router-dom";
import Results from "./Results";
import LeagueTable from "./LeagueTable";
import { device } from "../../objects/devices";
import styled from "styled-components";
import Slider from "./../Small Components/Slider";
import Sign from "./../Small Components/Sign";

const BodyDivStyle = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
  width: 100%;
  padding: 30px 5vw;
  overflow-x: hidden;
  overflow-y: scroll;
  @media ${device.mobileL} {
    padding: 15px 2vw;
  }
`;

const Body = () => {
  return (
    <BodyDivStyle>
      <Switch>
        <Route path="/">
          <Slider />
          <Results />
        </Route>
        <Route path="/sign" render={() => <Sign />} />
        <Route
          path="/premierleague"
          render={(props) => <LeagueTable {...props} />}
        />
      </Switch>
    </BodyDivStyle>
  );
};

export default Body;
