import React, { useState } from "react";
import styled from "styled-components";
import { testLib } from "../../objects/testLib";

const ContainerDivStyle = styled.div`
  display: flex;
  visibility: hidden !important;
  justify-content: center;
  align-items: center;
  position: relative;
  width: 100%;
  top: 0;
  background-color: #192c38;
  border-radius: 4px;
  padding: 5px;
  & input {
    color: rgba(255, 255, 255, 0.7);
    padding: 5px 5px;
    justify-content: flex-end;
    background-color: #213743;
    border-radius: 3px;
    width: 100%;
    min-width: 170px;
    border-color: transparent;
    outline: none;
  }
`;

const SearchDivStyle = styled.div`
  display: flex;
  visibility: visible !important;
  flex-direction: column;
  align-items: center;
  position: absolute;
  top: 0;
  width: 70%;
  background-color: #192c38;
  border-radius: 4px;
  padding: 5px;
  justify-content: center;
  animation: withreduce 0.5s ease-out;
  @keyframes withreduce {
    0% {
      width: 75%;
    }
    100% {
      width: 70%;
    }
  }
  &:hover {
    animation: withgrow 0.5s ease-out forwards;
    @keyframes withgrow {
      0% {
        width: 70%;
      }
      100% {
        width: 75%;
      }
    }
    box-shadow: 0px 0px 3px 0px rgba(0, 0, 0, 1);
  }
  & input {
    color: rgba(255, 255, 255, 0.7);
    padding: 5px 5px;
    justify-content: flex-end;
    background-color: #213743;
    border-radius: 3px;
    width: 100%;
    min-width: 170px;
    border-color: transparent;
    outline: none;
  }
`;

const ListDivStyle = styled.div`
  display: flex;
  flex-direction: column;
  padding: 10px 7px 0px 7px;
  width: 100%;
  & li {
    list-style: none;
    color: rgba(255, 255, 255, 0.8);
    font-size: 14px;
    margin: 2px 0px;
  }
`;

const SearchBar = () => {
  const [keyword, setkeyword] = useState("");
  const keywordChange = (e) => {
    setkeyword(e.target.value);
  };

  const ref = React.useRef();

  const test = testLib.filter((each) => each.includes(keyword));
  console.log(test);
  console.log(keyword);
  return (
    <ContainerDivStyle>
      <input
        type="text"
        onChange={keywordChange}
        placeholder="Search..."
        ref={ref}
      />
      <SearchDivStyle>
        <input
          type="text"
          onChange={keywordChange}
          value={keyword}
          placeholder="Search..."
          ref={ref}
        />
        <ListDivStyle>
          {test.map((each, i) => (
            <li key={i}>{each}</li>
          ))}
        </ListDivStyle>
      </SearchDivStyle>
    </ContainerDivStyle>
  );
};

export default SearchBar;
