const initialState = {
  match: {},
};

export const favMatchReducer = (state = initialState, action) => {
  switch (action.type) {
    case "SET_FAV_MATCH":
      return {
        ...state,
        user: {
          ...action.payload,
        },
      };
    default:
      return state;
  }
};
