export const favMatchReducer = (state = [], action) => {
  switch (action.type) {
    case "SET_FAV_MATCH":
      return [...state, action.payload].filter((match) => {
        if (match.id !== action.payload.id) {
          return match;
        }
      });
    default:
      return state;
  }
};
