import React from "react";
import styled from "styled-components";
import MdClose from "react-ionicons/lib/MdClose";
import Modal from "../Small Components/Modal";

const LoginDivStyle = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 20px;
  & form {
    font-size: 14px;
    display: flex;
    flex-direction: column;
    & span {
      margin: 5px 0px;
    }
    & input {
      color: #fff;
      font-size: 14px;
      background: #0f212e;
      box-shadow: none;
      outline-style: none;
      border-color: transparent;
      border-radius: 3px;
      padding: 3px;
      margin: 6px 0px;
    }
  }
`;

const CloseDivStyle = styled.div`
  display: flex;
  justify-content: flex-end;
  align-items: center;
`;

const Login = (props) => {
  const params = new URLSearchParams(props.location.search);
  const modalRef = React.createRef();

  const onLocalClose = () => {
    modalRef.current.close();
    props.history.push(props.location.pathname);
  };

  return (
    params.get("login") && (
      <Modal
        close={() => {
          props.history.push(props.location.pathname);
        }}
      >
        <CloseDivStyle>
          <MdClose
            style={{ cursor: "pointer" }}
            color="rgba(255, 255, 255, 0.6)"
            fontSize="18px"
            onClick={onLocalClose}
          />
        </CloseDivStyle>
        <LoginDivStyle>
          <form>
            <span>Email</span>
            <input type="text" />
            <span>Password</span>
            <input type="password" />
          </form>
        </LoginDivStyle>
      </Modal>
    )
  );
};

export default Login;

