import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchTodaysLeagueData } from "../../Redux/Actions";
import { device } from "../../objects/devices";
import FavMatches from "./FavMatches";
import League from "./League";
import styled from "styled-components";

const ResultsDivStyle = styled.div`
  display: flex;
  flex-direction: column;
  padding: 15px 2vw;
  align-items: center;
  position: relative;
  @media ${device.mobileL} {
    padding: 10px 2vw;
  }
`;

const Results = () => {
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchTodaysLeagueData());
  }, [dispatch]);

  const data = useSelector((state) => state.todaysLeagueData);

  const ver = data.api.fixtures;
  const ids = ver.map((elem) => {
    return elem.league.name;
  });

  const uniqueLeagues = ids.filter((item, pos) => {
    return ids.indexOf(item) == pos;
  });

  return (
    <ResultsDivStyle>
      <FavMatches />
      {data.api.results > 0 &&
        uniqueLeagues.map((elem, index) => <League key={index} name={elem} />)}
    </ResultsDivStyle>
  );
};

export default Results;
