import React, { useState } from "react";
import MdClose from "react-ionicons/lib/MdClose";
import styled from "styled-components";
import Modal from "react-modal";

const CloseDivStyle = styled.div`
  display: flex;
  justify-content: flex-end;
  align-items: center;
`;

const LoginDivStyle = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 20px;
  & form {
    font-size: 14px;
    display: flex;
    flex-direction: column;
    & span {
      margin: 5px 0px;
    }
    & input {
      color: #fff;
      font-size: 14px;
      background: #0f212e;
      box-shadow: none;
      outline-style: none;
      border-color: transparent;
      border-radius: 3px;
      padding: 3px;
      margin: 6px 0px;
    }
  }
`;

const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    width: "90vw",
    maxWidth: "560px",
    borderRadius: "5px",
    transform: "translate(-50%, -50%)",
    color: "#fff",
    background: "#1A2C38",
    border: "none",
    padding: "8px",
    zIndex: "999",
  },
  overlay: {
    position: "fixed",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: "rgba(14, 18, 36, 0.7)",
    zIndex: "998",
  },
};

const Sign = () => {
  const [isOpen, setisOpen] = useState(true);
  Modal.setAppElement("#webapp");

  const closeModal = () => {
    setisOpen(false);
  };

  return (
    <div>
      <Modal isOpen={isOpen} onRequestClose={closeModal} style={customStyles}>
        <CloseDivStyle>
          <MdClose
            style={{ cursor: "pointer" }}
            color="rgba(255, 255, 255, 0.6)"
            fontSize="18px"
            onClick={closeModal}
          />
        </CloseDivStyle>
        <LoginDivStyle>
          <form>
            <span>Email</span>
            <input type="text" />
            <span>Password</span>
            <input type="password" />
          </form>
        </LoginDivStyle>
      </Modal>
    </div>
  );
};

export default Sign;
