import { SET_TODAYS_LEAGUE_DATA } from "../Actions";

export const todayLeagueData = (state = { api: { leagues: [] } }, action) => {
  switch (action.type) {
    case SET_TODAYS_LEAGUE_DATA:
      return { ...state, ...action.payload };
    default:
      return state;
  }
};
