import React from "react";
import { Switch, Route } from "react-router-dom";
import Results from "./Results";
import LeagueTable from "./LeagueTable";
import { device } from "../../objects/devices";
import styled from "styled-components";
import Slider from "./../Small Components/Slider";
import Modal from "../Small Components/Modal";

const BodyDivStyle = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
  width: 100%;
  padding: 30px 5vw;
  overflow-x: hidden;
  overflow-y: scroll;
  @media ${device.mobileL} {
    padding: 15px 2vw;
  }
`;

const Body = () => {
  return (
    <BodyDivStyle>
      <Switch>
        <Route exact path="/">
          <Slider />
          <Results />
        </Route>
        <Route exacr path="/Modal" component={Modal} />
        <Route
          path="/premierleague"
          render={(props) => <LeagueTable {...props} />}
        />
      </Switch>
    </BodyDivStyle>
  );
};

export default Body;
