import { SET_TODAYS_LEAGUE_DATA } from "../Actions";

export const todaysLeagueData = (
  state = { api: { results: 0, fixtures: [] } },
  action
) => {
  switch (action.type) {
    case SET_TODAYS_LEAGUE_DATA:
      return {
        api: {
          results: state.api.results + action.payload.api.results,
          fixtures: [...state.api.fixtures, ...action.payload.api.fixtures],
        },
      };

    default:
      return state;
  }
};
