import React from "react";
import styled from "styled-components";

const ContainerDivStyle = styled.div`
  display: flex;
  height: 250px;
  background: #213743;
  box-shadow: 0px 0px 3px 1px rgba(0, 0, 0, 0.1);
  border-radius: 6px;
`;

const ImageDivStyle = styled.div`
  background-image: url("https://mediumrare.imgix.net/886b8e16e3d0b2c6c47e6ede61c21a8f87d21725456787e76e25d24211de0b62?auto=compress&fit=crop&w=512&h=221&dpr=1");
  background-size: cover;
  width: 50%;
  overflow: hidden;
  border-top-right-radius: 6px;
  border-bottom-right-radius: 6px;
`;

const TextDivStyle = styled.div`
  display: flex;
  align-items: center;
  color: #fff;
  width: 50%;
  padding: 15px 2%;
`;

const Slider = () => {
  return (
    <ContainerDivStyle>
      <TextDivStyle>
        <h4>The first headline</h4>
        <p>
          Place a bet on Winner markets of any UFC match, and earn your stake
          back if your selection loses by split decision! With no max payout,
          this is one of our biggest promotions yet.{" "}
        </p>
      </TextDivStyle>
      <ImageDivStyle />
    </ContainerDivStyle>
  );
};

export default Slider;
