import React, { useState } from "react";
import ReactDOM from "react-dom";
import styled from "styled-components";

const ModalWrapperStyle = styled.div`
  position: fixed;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
`;

const ModalBackdropStyle = styled.div`
  position: fixed;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  z-index: 100;
  background-color: rgba(0, 0, 0, 0.3);
`;

const ModalStyle = styled.div`
  position: relative;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  min-height: 30%;
  width: 60%;
  background-color: white;
  z-index: 101;
  overflow-y: auto;
  padding: 40px;
`;

const Modal = () => {
  const [display, setDisplay] = useState(true);

  const closeModal = () => {
    setDisplay(false);
  };

  return display ? (
    <ModalWrapperStyle>
      <ModalBackdropStyle onClick={closeModal}>
        <ModalStyle>
          <h1>First Header</h1>
          <p>
            Elit duis tempor nisi officia sit labore ea incididunt velit dolor
            sunt non veniam ut. Qui nulla dolore do non tempor. Laboris eu quis
            nisi anim aliqua est esse laborum. Magna occaecat aliqua consequat
            duis eu magna do aliqua eu.
          </p>
        </ModalStyle>
      </ModalBackdropStyle>
    </ModalWrapperStyle>
  ) : null;
};

export default Modal;
